CREATE TABLE public."User"(ID SERIAL, PRIMARY KEY (ID), ID_USER INTEGER NOT NULL, LOGIN VARCHAR(20) NOT NULL, PASSWORD VARCHAR(20) NOT NULL, LIST_CREATED_ROOMS INTEGER[], LIST_CHAT_ROOMS INTEGER[]);
CREATE TABLE public."Chatroom"(ID SERIAL, PRIMARY KEY (ID), ID_CHATROOM INTEGER NOT NULL, NAME VARCHAR(20) NOT NULL, OWNER INTEGER NOT NULL, LIST_MESSAGES INTEGER[]);
CREATE TABLE public."Message"(ID SERIAL, PRIMARY KEY (ID), ID_MESSAGE INTEGER NOT NULL, AUTHOR INTEGER NOT NULL, ROOM INTEGER NOT NULL, MESSAGE VARCHAR(20) NOT NULL, DATE_TIME TIMESTAMP NOT NULL);